func reverseString(s []byte) {
	left, right := 0, len(s)-1
	for left < right {
		s[left], s[right] = s[right], s[left]
		left++
		right--
	}
}

func reverse(x int) int {
	xStr := []byte(strconv.Itoa(x))
	neg := false
	if xStr[0] == '-' {
		neg = true
	}
	reverseString(xStr)
	res := 0
	if neg {
		res, _ = strconv.Atoi(string(xStr[:len(xStr)-1]))
		res = -res
	} else {
		res, _ = strconv.Atoi(string(xStr))
	}
	if res >= 1<<31 || res <= -1<<31-1 {
		return 0
	}
	return res
}

func firstUniqChar(s string) int {
	for i := range s {
		found := false
		for j := range s {
			if s[i] == s[j] && i != j {
				found = true
				break
			}
		}
		if !found {
			return i
		}
	}

	return -1
}

func isAnagram(s string, t string) bool {
	sN, tN := len(s), len(t)
	if sN != tN {
		return false
	}
	sMap := make(map[rune]int)
	tMap := make(map[rune]int)
	wg := sync.WaitGroup{}
	wg.Add(2)
	go func() {
		defer wg.Done()
		for _, v := range s {
			sMap[v]++
		}
	}()
	go func() {
		defer wg.Done()
		for _, v := range t {
			tMap[v]++
		}
	}()
	wg.Wait()

	for k, v := range sMap {
		if tMap[k] != v {
			return false
		}
	}

	return true
}

func isPalindrome(s string) bool {
	s = strings.ToLower(s)
	re := regexp.MustCompile("[a-z0-9]*")
	splited := re.FindAllString(s, -1)
	s = strings.Join(splited, "")

	if len(s) <= 1 {
		return true
	}
	left, right := 0, len(s)-1
	for left < right {
		if s[left] != s[right] {
			return false
		}
		left++
		right--
	}

	return true
}

func isPalindrome2(s string) bool {
	sCopied := ""
	for _, c := range s {
		switch {
		case 48 <= c && c <= 57:
			sCopied += string(c)
		case 65 <= c && c <= 90:
			sCopied += string(c + 32)
		case 97 <= c && c <= 122:
			sCopied += string(c)
		}
	}

	if len(sCopied) <= 1 {
		return true
	}
	left, right := 0, len(sCopied)-1
	for left < right {
		if sCopied[left] != sCopied[right] {
			return false
		}
		left++
		right--
	}

	return true
}

func myAtoi(s string) int {
	re := regexp.MustCompile("-?[0-9]+")
	s = re.FindString(s)
	res, _ := strconv.Atoi(s)
	return res
}

func pNext(s string) []int {
	n := len(s)
	res := make([]int, n)
	res[0] = -1
	i, k := 0, -1
	for i < n-1 {
		if k == -1 || s[i] == s[k] {
			i, k = i+1, k+1
			res[i] = k
		} else {
			k = res[k]
		}
	}

	return res
}
func strStr(haystack string, needle string) int {
	next := pNext(needle)
	fmt.Println(next)
	idx := 0
	i := 0
	j := 0
	for i < len(haystack) && j < len(needle) {
		if j == -1 || haystack[i] == needle[j] {
			i++
			j++
			continue
		}
		idx = i - next[j]
		j = next[j]
	}
	if j < len(needle) {
		return -1
	}
	return idx
}

func countAndSay(n int) string {
	if n == 1 {
		return "1"
	}
	lastOne := countAndSay(n - 1)
	length := len(lastOne)
	sets := []string{}
	k := 0
	for i := 1; i < length; i++ {
		if lastOne[i] == lastOne[i-1] {
			continue
		}
		sets = append(sets, lastOne[k:i])
		k = i
	}
	sets = append(sets, lastOne[k:])
	output := ""
	for _, v := range sets {
		setLen := len(v)
		output += strconv.Itoa(setLen) + v[0:1]
	}
	return output
}

